import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router'
import axios from 'axios'
import { address, data } from '../data/data'
import { NavLink } from 'react-router-dom'
import { MemoryRouter as Router } from "react-router";
import V from '../../assets/images/v.svg'
import X from '../../assets/images/x.jpg'

export const ApproveDoc = () => {
    const { id } = useParams()
    const [document, setDocument] = useState([])
    const [submitted, setSubmitted] = useState(1)
    const [fields, setFields] = useState([])
    const [comment, setComment] = useState("")

    useEffect(() => {
        const config = {
            method: 'get',
            url: `${address.use}/v1/api/document/approval/${id}`,
            headers: {
                'Authorization': localStorage.getItem("token")
            }
        };
        axios(config)
            .then(function (response) {
                setDocument(response.data)
                setFields(response.data.fields)
            })
            .catch(function (error) {
                console.log(error)
            });
    }, [])

    const handleApprove = () => {
        const config = {
            method: 'get',
            url: `${address.use}/v1/api/document/progress/${id}`,
            headers: {
                'Authorization': localStorage.getItem("token")
            }
        };
        axios(config)
            .then(function (response) {
                console.log(JSON.stringify(response.data));
                setSubmitted(0)
            })
            .catch(function (error) {
                console.log(error);
            });
        console.log("approved")
    }

    const handlePreDecline = () => {
        setSubmitted(2)
    }

    const handleBack = () => {
        setSubmitted(5)
    }

    const handleChange = (e) => {
        setComment(e.target.value)
    }

    const handleDecline = () => {
        const data = JSON.stringify({
            "comment": comment
          });

        const config = {
            method: 'post',
            url: `${address.use}/v1/api/document/decline/${id}`,
            headers: {
                'Authorization': localStorage.getItem("token"),
                'Content-Type': 'application/json'
            },
            data : data
        }
        axios(config)
            .then(function (response) {
                console.log(JSON.stringify(response.data));
                setSubmitted(4)
            })
            .catch(function (error) {
                console.log(error);
            })
        console.log("approved")
    }

    const handleClose = () => {
        window.open("about:blank", "_self");
        window.close();
    }

    let counter = 0

    if (submitted === 0) {
        return (
            <div>
                <div className="container">
                    <div className="contacts__created">
                        <img src={V} alt="done" width="150px" />
                        <span>Document has been approved!</span>
                        <button className="contacts__close" onClick={handleClose}>Close this page X</button>
                    </div>
                </div>
            </div>
        )
    }
    else if (submitted === 2) {
        return (
            <div>
                <div className="container">
                    <div className="contacts__created">
                        <img src={X} alt="done" width="150px" />
                        <span>Please describe the reason for the refusal:</span>
                        <textarea cols="40" rows="4" style={{margin : "20px", fontSize : "20px"}} onInput={handleChange}></textarea>
                        <div>
                            <button className="contacts__cancel" onClick={handleBack} >Cancel</button>
                            <button className="contacts__close" onClick={handleDecline} style={{marginLeft: "15px"}}>Continue</button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
    else if (submitted === 4) {
        return (
            <div>
                <div className="container">
                    <div className="contacts__created">
                        <img src={X} alt="done" width="150px" />
                        <span>Document has been rejected!</span>
                        <button className="contacts__close" onClick={handleClose}>Close this page X</button>
                    </div>
                </div>
            </div>
        )
    }
     else {
        return (
            <div className="contacts__page">
                <div className="container">
                    <Router>
                        <div className="contacts__header">
                            <a href="/" className="contacts__header-logo contacts__header-item">
                                <i className="fas fa-home" />
                            </a>
                            <a href="/" className="contacts__header-item"> Homepage</a>
                            <div className="contacts__header-item contacts__header-logo">
                                <i className="fas fa-angle-right" />
                            </div>
                            <NavLink to='/address' className="contacts__header-item">
                                {document?.document?.name}
                            </NavLink>
                        </div>
                    </Router>
                    <div className="document">
                        <p className="document__approval">
                            Approval Content
                        </p>
                        <form>
                            <div className="document__cover">
                                {
                                    <div className="document__title">
                                        {
                                            document?.document?.name
                                        }
                                    </div>
                                }
                                <div className="document__rows">
                                    {
                                        document?.fields?.map((el, idx, arr) => (
                                            <div className={`${el.half ? "document__row-half" : "document__row-full"}`}
                                                key={idx}>
                                                <div className="document__hidden">
                                                    {
                                                        counter += el.count
                                                    }
                                                </div>
                                                {
                                                    counter % 2 > 0 && arr[idx + 1] && arr[idx + 1] !== 0 && !arr[idx + 1].half
                                                    &&
                                                    <div className="document__row-empty">
                                                        <div className="document__hidden">
                                                            {
                                                                counter = counter + 1
                                                            }
                                                        </div>
                                                    </div>
                                                }
                                                {
                                                    counter % 2 > 0 && el.last
                                                    &&
                                                    <div className="document__row-empty">
